import Header from "../components/Header";
import Footer from "../components/Footer";
import "./HomePage.css";
import ApprovalSwitch from "../components/ApprovalSwitch";
import DeleteListingButton from "../components/DeleteListingButton";
import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Box } from "@mui/material";

axios.defaults.withCredentials = true;

const AdminDashboard = () => {

  const navigate = useNavigate();
  const [opportunities, setOpportunities] = useState({});
  const [pastProjects, setPastProjects] = useState({});
  const [availableTech, setAvailableTech] = useState({});

  useEffect(() => {
    // get all opportunities
    axios
      .get("http://localhost:5500/project-connect/browse")
      .then((response) => {
        console.log(response);
        if (response.data.auth === false) {
          // if jwt has expired
          navigate("/expired");
        } else {
          setOpportunities(response.data);
        }
      })
      .catch((err) => {
        console.log(err);
        navigate("/Error");
      });

      // get all past projects
      axios
      .get("http://localhost:5500/past-projects/browse")
      .then((response) => {
        console.log(response);
        if (response.data.auth === false) {
          // if jwt has expired
          navigate("/expired");
        } else {
          setPastProjects(response.data);
        }
      })
      .catch((err) => {
        console.log(err);
        navigate("/Error");
      });

      // get all available tech
      axios
      .get("http://localhost:5500/available-tech/browse")
      .then((response) => {
        console.log(response);
        if (response.data.auth === false) {
          // if jwt has expired
          navigate("/expired");
        } else {
          setAvailableTech(response.data);
        }
      })
      .catch((err) => {
        console.log(err);
        navigate("/Error");
      });

  }, [navigate]);

  //Function to convert the date to a more readable format
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = String(date.getDate()).padStart(2, "0");
    const month = String(date.getMonth() + 1).padStart(2, "0"); // Months are zero-based, so we need to add 1
    const year = date.getFullYear();
    return `${day}-${month}-${year}`;
  };

  return (
    <main className="home-page">
      <Header />

      <ApprovalSwitch listingID={15} />
      <DeleteListingButton listingID={15} />

      <section>
        <h1>Past Projects</h1>
        {pastProjects?.map((listing, index) => (
          <Box
            key={listing.listingID}
            className="listing-container"
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "stretch",
              width: "55%",
              minHeight: "fit-content",
              margin: 7,
              paddingBottom: 5,
            }}
            bgcolor="#00808010"
            borderRadius="25px"
          >
            <Box sx={{ fontSize: "60%", marginLeft: 5, marginRight: 5 }}>
              <h3>{listing.listingTitle}</h3>
            </Box>

            <Box
              sx={{
                display: "flex",
                alignItems: "center",
                justifyContent: "space-around",
              }}
            >
              <Box
                sx={{
                  fontSize: "30%",
                  marginLeft: 2,
                  marginRight: 0,
                  padding: 2,
                  paddingRight: 7,
                  width: "fit-content",
                  maxWidth: 500,
                }}
                bgcolor="#ffffff"
                borderRadius="25px"
              >
                <p>
                  <span className="weighty">Opportunity Type: </span>{" "}
                  {listing.opportunityType}
                </p>
                <p>
                  <span className="weighty">Start Date: </span>
                  {formatDate(listing.startDate)}
                </p>
                <p>
                  <span className="weighty">Sector: </span> {listing.sector}
                </p>
                <p>
                  <span className="weighty">Duration: </span> {listing.duration}
                </p>
                <p>
                  <span className="weighty">Requirements: </span>{" "}
                  {listing.requirements}
                </p>
                <p>
                  <span className="weighty">Salary: </span> {listing.salary}
                </p>
                <p>
                  <span className="weighty">Apply By: </span>{" "}
                  {formatDate(listing.applyBy)}
                </p>
                {/*<p><span className="weighty">Contact Email: </span> {listing.contactemail}</p>*/}
              </Box>

              <Box sx={{ borderRadius: "25px", width: "35%", marginRight: 2 }}>
                {listing.imageLink && (
                  <img
                    src={listing.imageLink}
                    alt="Listing"
                    style={{
                      maxWidth: "100%",
                      height: "auto",
                      borderRadius: "25px",
                      width: "auto",
                      objectFit: "cover",
                    }}
                  />
                )}
              </Box>
            </Box>

            <Box
              sx={{
                fontSize: "30%",
                marginLeft: 5,
                marginRight: 5,
                marginTop: 5,
                padding: 2,
              }}
              bgcolor="#ffffff"
              borderRadius="25px"
            >
              <p className="weighty-ul">Role Description:</p>
              <p>{listing.opportunityDescription}</p>
            </Box>
          </Box>
        ))}
      </section>

      <Footer />
    </main>
  );
};

export default AdminDashboard;
