import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import {
  TextField,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
} from "@mui/material";
import PersonAddIcon from "@mui/icons-material/PersonAdd";
import PersonRemoveIcon from "@mui/icons-material/PersonRemove";
import Container from "@mui/material/Container";
import Header from "../components/Header";
//import Footer from "../components/Footer";
import { Button } from "@mui/material";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./SubmitPastProject.css";

// axios.defaults.withCredentials = true;

const SubmitPastProject = () => {
  const [title_input, setTitle] = useState("");
  const [abs_input, setAbstract] = useState("");
  const [evolution, setEvolution] = useState("");
  const [journey, setJourney] = useState("");
  const [github, setGithub] = useState("");
  const [image, setImage] = useState("");
  const [video, setVideo] = useState("");

  //sectors dropdown
  const [sectors, setSectors] = useState([]);
  const [selectedSector, setSelectedSector] = useState("");

  //productType dropdown
  const [product, setProduct] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState("");

  //remaining characters variables
  const [abs_remainingChars, setAbsRemainingChars] = useState(1000);
  const [evo_remainingChars, setEvoRemainingChars] = useState(1000);
  const [journ_remainingChars, setJournRemainingChars] = useState(1000);

  //navigate back to home page on submit
  const navigate = useNavigate();

  //get sectors for dropdown
  useEffect(() => {
    axios
      .get(`http://localhost:5500/form-options/sectors`)
      .then((response) => {
        setSectors(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleSectorChange = (event) => {
    setSelectedSector(event.target.value);
  };

  //get productType for dropdown
  useEffect(() => {
    axios
      .get(`http://localhost:5500/form-options/product-types`)
      .then((response) => {
        setProduct(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleProductChange = (event) => {
    setSelectedProduct(event.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    //Form Validation

    if (!selectedSector || !selectedProduct) {
      alert("Please fill in all required fields marked with an asterisk (*).");
      return;
    }

    if (!title_input || !abs_input) {
      alert("Please fill in all required fields marked with an asterisk (*).");
      return;
    }

    axios
      .post(`http://localhost:5500/past-projects/submit-form`, {
        abstract: abs_input,
        title: title_input,
        sector: selectedSector,
        productEvolution: evolution,
        publishingJourney: journey,
        githubLink: github,
        productType: selectedProduct,
        imageLink: image, //check routes and db functions are handling this
        videoLink: video, //check routes and db functions are handling this
        members: teamMembers,
      })
      .then(function (response) {
        console.log(`response: ${response}`);
        navigate("/");
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  //handlers for character countdown
  const handleAbstractInput = (e) => {
    const input = e.target.value;
    if (input.length <= 1000) {
      setAbstract(input);
      setAbsRemainingChars(1000 - input.length);
    }
  };

  const handleEvolutionInput = (e) => {
    const input = e.target.value;
    if (input.length <= 1000) {
      setEvolution(input);
      setEvoRemainingChars(1000 - input.length);
    }
  };

  const handleJourneyInput = (e) => {
    const input = e.target.value;
    if (input.length <= 1000) {
      setJourney(input);
      setJournRemainingChars(1000 - input.length);
    }
  };

  //Render Team members: minimum of 1, maximum of 3 with add+remove feature
  const [teamMembers, setTeamMembers] = useState([
    {
      firstName: "",
      lastName: "",
      roleTitle: "",
      roleDesc: "",
      linkedin: "",
    },
  ]);

  const handleTeamMemberChange = (index, field, value) => {
    const newTeamMembers = [...teamMembers];
    newTeamMembers[index][field] = value;
    setTeamMembers(newTeamMembers);
  };

  const renderTeamMemberFields = (index) => {
    return (
      <div key={index}>
        <h4 className="member">Team Member {index + 1}</h4>
        <div>
          <TextField
            sx={{
              borderRadius: "15px",
              "& .MuiOutlinedInput-root": {
                borderRadius: "20px",
                "& fieldset": {
                  borderColor: "transparent",
                },
                "&:hover fieldset": {
                  borderColor: "transparent",
                },
                "&.Mui-focused fieldset": {
                  borderColor: "transparent",
                },
              },
              backgroundColor: "white",
            }}
            style={{ width: 330 }}
            multiline
            fullWidth
            id="outlined-required"
            label={`Team Member ${index + 1}: First Name`}
            defaultValue=""
            maxRows={Infinity}
            value={teamMembers[index].firstName}
            onInput={(e) =>
              handleTeamMemberChange(index, "firstName", e.target.value)
            }
          />

          <TextField
            sx={{
              left: 40,
              borderRadius: "15px",
              "& .MuiOutlinedInput-root": {
                borderRadius: "20px",
                "& fieldset": {
                  borderColor: "transparent",
                },
                "&:hover fieldset": {
                  borderColor: "transparent",
                },
                "&.Mui-focused fieldset": {
                  borderColor: "transparent",
                },
              },
              backgroundColor: "white",
            }}
            style={{ width: 330 }}
            multiline
            fullWidth
            id="outlined-required"
            label={`Team Member ${index + 1}: Last Name`}
            defaultValue=""
            maxRows={Infinity}
            value={teamMembers[index].lastName}
            onInput={(e) =>
              handleTeamMemberChange(index, "lastName", e.target.value)
            }
          />
        </div>

        <div>
          <TextField
            sx={{
              borderRadius: "15px",
              "& .MuiOutlinedInput-root": {
                borderRadius: "20px",
                "& fieldset": {
                  borderColor: "transparent",
                },
                "&:hover fieldset": {
                  borderColor: "transparent",
                },
                "&.Mui-focused fieldset": {
                  borderColor: "transparent",
                },
              },
              backgroundColor: "white",
            }}
            style={{ width: 330 }}
            multiline
            fullWidth
            id="outlined-required"
            label={`Team Member ${index + 1}: Role Title`}
            defaultValue=""
            maxRows={Infinity}
            value={teamMembers[index].roleTitle}
            onInput={(e) =>
              handleTeamMemberChange(index, "roleTitle", e.target.value)
            }
          />

          <TextField
            sx={{
              left: 40,
              borderRadius: "15px",
              "& .MuiOutlinedInput-root": {
                borderRadius: "20px",
                "& fieldset": {
                  borderColor: "transparent",
                },
                "&:hover fieldset": {
                  borderColor: "transparent",
                },
                "&.Mui-focused fieldset": {
                  borderColor: "transparent",
                },
              },
              backgroundColor: "white",
            }}
            style={{ width: 330 }}
            multiline
            fullWidth
            id="outlined-required"
            label={`Team Member ${index + 1}: Linkedin Profile Link`}
            defaultValue=""
            maxRows={Infinity}
            value={teamMembers[index].linkedin}
            onInput={(e) =>
              handleTeamMemberChange(index, "linkedin", e.target.value)
            }
          />
        </div>

        <div>
          <TextField
            sx={{
              borderRadius: "15px",
              "& .MuiOutlinedInput-root": {
                borderRadius: "20px",
                "& fieldset": {
                  borderColor: "transparent",
                },
                "&:hover fieldset": {
                  borderColor: "transparent",
                },
                "&.Mui-focused fieldset": {
                  borderColor: "transparent",
                },
              },
              backgroundColor: "white",
            }}
            style={{ width: 700 }}
            multiline
            fullWidth
            rows="5"
            id="outlined-required"
            label={`Team Member ${index + 1}: Role Description`}
            defaultValue=""
            maxRows={Infinity}
            value={teamMembers[index].roleDesc}
            onInput={(e) =>
              handleTeamMemberChange(index, "roleDesc", e.target.value)
            }
          />
        </div>
      </div>
    );
  };

  const addTeamMember = () => {
    if (teamMembers.length < 3) {
      setTeamMembers([
        ...teamMembers,
        {
          firstName: "",
          lastName: "",
          roleTitle: "",
          roleDesc: "",
          linkedin: "",
        },
      ]);
    }
  };

  const removeTeamMember = () => {
    if (teamMembers.length > 1) {
      setTeamMembers(teamMembers.slice(0, teamMembers.length - 1));
    }
  };

  const LineComp = () => {
    return <div className="LineComp" />;
  };

  return (
    <main className="submit-tech">
      <h1 className="title-text">Submit a Success Story</h1>
      <h2 className="sub-head">
        Create profiles on previously published software projects.
      </h2>
      <LineComp />

      <Header />
      <Container
        maxWidth="md"
        sx={{ paddingTop: 5, marginLeft: "7%", paddingBottom: 5 }}
      >
        <Box
          className="main-box"
          bgcolor="#00808010"
          borderRadius={4}
          component="form"
          noValidate
          autoComplete="off"
          onSubmit={handleSubmit}
        >
          <form className="form-container" action="" id="pastProjectForm">
            <div>
              <TextField
                Title
                sx={{
                  borderRadius: "15px",
                  "& .MuiOutlinedInput-root": {
                    borderRadius: "20px",
                    "& fieldset": {
                      borderColor: "transparent",
                    },
                    "&:hover fieldset": {
                      borderColor: "transparent",
                    },
                    "&.Mui-focused fieldset": {
                      borderColor: "transparent",
                    },
                  },
                  backgroundColor: "white",
                }}
                style={{ width: 700 }}
                multiline
                fullWidth
                id="outlined-required"
                label="Title"
                defaultValue="Title"
                required="required"
                maxRows={Infinity}
                value={title_input}
                onInput={(e) => setTitle(e.target.value)}
              />
            </div>

            <div className="drop">
              <FormControl
                sx={{
                  borderRadius: "15px",
                  "& .MuiOutlinedInput-root": {
                    borderRadius: "20px",
                    "& fieldset": {
                      borderColor: "transparent",
                    },
                    "&:hover fieldset": {
                      borderColor: "transparent",
                    },
                    "&.Mui-focused fieldset": {
                      borderColor: "transparent",
                    },
                  },
                  backgroundColor: "white",
                }}
                required="required"
              >
                <InputLabel id="sector-label">Select Sector</InputLabel>
                <Select
                  labelId="sector-label"
                  id="sector-select"
                  defaultValue=""
                  style={{ width: 330 }}
                  value={selectedSector}
                  onChange={handleSectorChange}
                >
                  {sectors.map((sectorObj, index) => (
                    <MenuItem key={index} value={sectorObj.sector}>
                      {sectorObj.sector}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>

              <FormControl
                sx={{
                  left: 40,
                  borderRadius: "15px",
                  "& .MuiOutlinedInput-root": {
                    borderRadius: "20px",
                    "& fieldset": {
                      borderColor: "transparent",
                    },
                    "&:hover fieldset": {
                      borderColor: "transparent",
                    },
                    "&.Mui-focused fieldset": {
                      borderColor: "transparent",
                    },
                  },
                  backgroundColor: "white",
                }}
                required="required"
              >
                <InputLabel id="product-label">Select Product Type</InputLabel>
                <Select
                  labelId="product-label"
                  id="product-select"
                  defaultValue=""
                  style={{ width: 330 }}
                  value={selectedProduct}
                  onChange={handleProductChange}
                >
                  {product.map((productTypeObj, index) => (
                    <MenuItem key={index} value={productTypeObj.productType}>
                      {productTypeObj.productType}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </div>

            <div>
              <TextField
                Abstract
                sx={{
                  borderRadius: "15px",
                  "& .MuiOutlinedInput-root": {
                    borderRadius: "20px",
                    "& fieldset": {
                      borderColor: "transparent",
                    },
                    "&:hover fieldset": {
                      borderColor: "transparent",
                    },
                    "&.Mui-focused fieldset": {
                      borderColor: "transparent",
                    },
                  },
                  backgroundColor: "white",
                }}
                style={{ width: 700 }}
                multiline
                fullWidth
                rows="5"
                id="outlined-required"
                label="Product Description (Abstract)"
                defaultValue="Abstract"
                required="required"
                value={abs_input}
                onInput={handleAbstractInput}
              />
            </div>

            <div className="char-count">
              {abs_remainingChars} characters remaining
            </div>

            <div>
              <TextField
                productEvolution
                sx={{
                  borderRadius: "15px",
                  "& .MuiOutlinedInput-root": {
                    borderRadius: "20px",
                    "& fieldset": {
                      borderColor: "transparent",
                    },
                    "&:hover fieldset": {
                      borderColor: "transparent",
                    },
                    "&.Mui-focused fieldset": {
                      borderColor: "transparent",
                    },
                  },
                  backgroundColor: "white",
                }}
                style={{ width: 700 }}
                multiline
                fullWidth
                rows="5"
                id="outlined-required"
                label="Product Evolution"
                defaultValue="Product Evolution"
                required="required"
                value={evolution}
                onInput={handleEvolutionInput}
              />
            </div>

            <div className="char-count">
              {evo_remainingChars} characters remaining
            </div>

            <div>
              <TextField
                imageLink
                sx={{
                  borderRadius: "15px",
                  "& .MuiOutlinedInput-root": {
                    borderRadius: "20px",
                    "& fieldset": {
                      borderColor: "transparent",
                    },
                    "&:hover fieldset": {
                      borderColor: "transparent",
                    },
                    "&.Mui-focused fieldset": {
                      borderColor: "transparent",
                    },
                  },
                  backgroundColor: "white",
                }}
                style={{ width: 330 }}
                multiline
                id="outlined-required"
                label="Submit an image link"
                defaultValue=""
                maxRows={Infinity}
                value={image}
                onInput={(e) => setImage(e.target.value)}
              />

              <TextField
                videoLink
                sx={{
                  left: 40,
                  borderRadius: "15px",
                  "& .MuiOutlinedInput-root": {
                    borderRadius: "20px",
                    "& fieldset": {
                      borderColor: "transparent",
                    },
                    "&:hover fieldset": {
                      borderColor: "transparent",
                    },
                    "&.Mui-focused fieldset": {
                      borderColor: "transparent",
                    },
                  },
                  backgroundColor: "white",
                }}
                style={{ width: 330 }}
                multiline
                id="outlined-required"
                label="Submit a YouTube link"
                defaultValue=""
                maxRows={Infinity}
                value={video}
                onInput={(e) => setVideo(e.target.value)}
              />
            </div>

            <div>
              <TextField
                githubLink
                sx={{
                  borderRadius: "15px",
                  "& .MuiOutlinedInput-root": {
                    borderRadius: "20px",
                    "& fieldset": {
                      borderColor: "transparent",
                    },
                    "&:hover fieldset": {
                      borderColor: "transparent",
                    },
                    "&.Mui-focused fieldset": {
                      borderColor: "transparent",
                    },
                  },
                  backgroundColor: "white",
                }}
                style={{ width: 700 }}
                multiline
                fullWidth
                id="outlined-required"
                label="Github Link"
                defaultValue=""
                maxRows={Infinity}
                value={github}
                onInput={(e) => setGithub(e.target.value)}
              />
            </div>

            <div>
              <TextField
                publishingJourney
                sx={{
                  borderRadius: "15px",
                  "& .MuiOutlinedInput-root": {
                    borderRadius: "20px",
                    "& fieldset": {
                      borderColor: "transparent",
                    },
                    "&:hover fieldset": {
                      borderColor: "transparent",
                    },
                    "&.Mui-focused fieldset": {
                      borderColor: "transparent",
                    },
                  },
                  backgroundColor: "white",
                }}
                style={{ width: 700 }}
                multiline
                fullWidth
                rows="5"
                id="outlined-required"
                label="Publishing Journey"
                defaultValue="Publishing Journey"
                required="required"
                value={journey}
                onInput={handleJourneyInput}
              />
            </div>

            <div className="char-count">
              {journ_remainingChars} characters remaining
            </div>

            {teamMembers.map((_, index) => renderTeamMemberFields(index))}

            <div>
              {teamMembers.length < 3 && (
                <Button
                  sx={{ color: "black" }}
                  onClick={addTeamMember}
                  startIcon={<PersonAddIcon />}
                >
                  Add Team Member
                </Button>
              )}
              {teamMembers.length > 1 && (
                <Button
                  sx={{ color: "black", left: 20 }}
                  onClick={removeTeamMember}
                  startIcon={<PersonRemoveIcon />}
                >
                  Remove Team Member
                </Button>
              )}
            </div>
          </form>

          <Button
            sx={{ width: 267, height: 50, left: 220, fontWeight: "bold" }}
            variant="contained"
            color="primary"
            type="submit"
          >
            Submit
          </Button>
        </Box>
      </Container>

      {/*<Footer/>*/}
    </main>
  );
};

export default SubmitPastProject;
