import "../pages/HomePage.css";
import { useState } from "react";
import { Switch, FormControlLabel } from "@mui/material";
import axios from "axios";

axios.defaults.withCredentials = true;

const ApprovalSwitch = (props) => {
  const [approved, setApproved] = useState(false);

  const handleToggle = () => {
    const listingID = props.listingID;

    if (!approved) {
      const approval = 1
      // If not approved set request to approve (approval = 1)
    } else {
      const approval = 0
      // call unapprove listing DB function
    }
    axios
        .patch(`http://localhost:5500/admin/listings/approval/${listingID}`, {approval: 1})
        .then((response) => {})
        .catch((err) => {});
    // change state
    setApproved(!approved);
  };

  return (
    <main className="home-page">
      <FormControlLabel
        control={
          <Switch
            checked={approved}
            onChange={handleToggle}
            color="primary"
            name="approvalSwitch"
            inputProps={{ "aria-label": "approve or disapprove" }}
          />
        }
        label={approved ? "approved" : "unapproved"}
      />
    </main>
  );
};

export default ApprovalSwitch;
