import React, { useState, useEffect } from 'react';
import { useRef } from "react";
import { useParams } from "react-router-dom";
import { Box, Button, Divider, Breadcrumbs, Typography } from "@mui/material";
import { Link } from 'react-router-dom';
import Header from "../components/Header";
import Footer from "../components/Footer"
import axios from 'axios';
import "./DetailedAvailTech.css";

const DetailedAvailTech = () => {
  const [listing, setListing] = useState(null);
  const [teamMembers, setTeamMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { id } = useParams();

  const svgIcon = (
    <svgIcon>
      <img alt='' src = "../../public/icon_button.svg"/>
    </svgIcon>
  )

  const svgIcon2 = (
    <svgIcon2>
      <img alt='' src="../../public/icon2.svg" />
    </svgIcon2>
  )

  const svgIcon3 = (
    <svgIcon3>
      <img alt="" src="../../public/download.svg" />
    </svgIcon3>
  )

  const ourProductSection = useRef(null);
  const publishingSection = useRef(null);
  const aboutSection = useRef(null);

  const scrollDown = () => {
    window.scrollTo({
      top: ourProductSection.current.offsetTop,
      behavior: 'smooth',
    });
  };

  const scrollDown2 = () => {
    window.scrollTo({
      top: publishingSection.current.offsetTop,
      behavior: 'smooth',
    });
  };

  const scrollDown3 = () => {
    window.scrollTo({
      top: aboutSection.current.offsetTop,
      behavior: 'smooth',
    });
  };

  function extractYouTubeVideoId(url) {
    const regex = /(?:\?v=|\/embed\/|youtu\.be\/)([0-9A-Za-z_-]{11})/;
    const matches = url.match(regex);
    return matches ? matches[1] : null;
  }

  //fetch listing data
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`http://localhost:5500/available-tech/browse/${id}`);
        setListing(response.data[0][0]);
        setTeamMembers(response.data[1]);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError(error);
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  console.log(listing);
  console.log(teamMembers);

  // Detailed listing information
  return (
    <main className="detailed-avail-tech">
    <div>

      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error fetching data</p>
      ) : listing ? (
        <div>
           <Box

            key={listing.listingID} 
            className="listing-container" 
            sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'stretch',
            width: '100%',
            minHeight: 'fit-content',
            margin: '10%',
            marginLeft:'25%',
            paddingBottom:5,
            }}
            borderRadius='25px'>

            <Header />

            <Box sx={{ marginBottom: 1 }}>
              <Breadcrumbs aria-label="breadcrumb">
